INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (19, 'Museo de la Ciudad', 'El Museo de la Ciudad se inauguró el 23 de julio de 1998 en las instalaciones que desde 1565 hasta 1974 fueron el Antiguo Hospital San Juan de Dios. Rehabilitada por la Empresa del Centro Histórico y el FONSAL durante un periodo de cuatro años. esta edificación constituye uno de los mayores bienes patrimoniales que conserva la capital.', 'Adulto 3.00. Estudiantes establecimientos municipales 1.00. Estudiantes con Carnét (+12 años) 2.00. Recorrido en inglés 4.00. Niños (3 a 11 años) y Tercera edad (+65) 1.50. Capacidades especiales y Centros de Rehabilitación Libre', '"Martes a domingo: 09:00 a 17:00. último Ingreso 16h00"', 'N/A', '5', -0.222826, -78.515021000000004, 'museoCiudad.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (20, 'Convento San Francisco:Museo Fray Pedro Gocial', 'Museo de Arte Religioso perteneciente a la Orden de Frailes Menores del Ecuador. Se exhibe en exposición permanente lienzos y esculturas de los siglos XVII – XVIII – XIX de la llamada Escuela Quiteña. Seis salas de exposición. dos galerías. el Claustro más grande de América y para finalizar el Coro de la Iglesia. constituyen los sitios que este museo reserva para sus visitantes.', 'Adulto Nacional o Extranjero 3.00. Niños 1.00. Estudiantes con carnét y Tercera Edad 2.00', '"Lunes a sabado: 09:00 a 17:00. Domingos: 09:00 a 13:00"', 'N/A', '1', -0.220169, -78.514849999999996, 'museoConvento.jpeg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (21, 'Museo Casa Sucre', '"En la Casa de Sucre se presenta una museografía que relata la vida de Sucre en un contexto humano y social. El lugar guarda los objetos personales y enseres del Mariscal de Ayacucho y de su esposa Mariana Carcelén. El lugar se divide en dos plantas y un patio central con pileta tradicional de piedra colonial."', 'Entrada Libre', 'Martes a domingo: 09:00 a 16h30', 'N/A', '1', -0.22187000000000001, -78.513233, 'museoSucre.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (22, 'Convento Santo Domingo: Museo Fray Pedro Bedón', 'El Museo fue inaugurado como tal el 6 de noviembre de 1965. siendo Prior el P. Luis Tipan Rojas. aprovechando la madera y tableros del piso del templo. En este momento estamos en la Primera Sala. usted podra? apreciar pinturas y esculturas de los siglos XVII y XVIII en su mayori?a de autores anonimos de la Escuela Quiteña y Europea y de su maximo representante Fray. ', 'Adulto Nacional o Extranjero 3.00. Niño. tercera edad. discapacitados. estudiantes escuelas y colegios 1.00', 'Lunes a Viernes de 9:00 a 13:30 y de 14:30 a 17:00. Sábados 9:00 a 14:00', 'N/A', '1', -0.223633, -78.512038000000004, 'museoFray.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (23, 'Museo Catedral Primada de Quito', 'El museo presenta una colección de obras de arte de la Escuela Quiteña tanto en pintura como en escultura. cuyos autores fueron los artistas más renombrados de la cuidad colonial. Miguel de Santiago. Caspicara. Manuel de Samaniego. El Padre Carlos. Bernardo Rodríguez. entre otros importantes artistas de los siglos XVII. XVIII. XIX. y XX. ', 'Adulto Extranjero 3.00. Adulto Nacional 2.00. Niños y Tercera edad 1.00. Recorrido de cúpulas 4.00 NAC y 6.00 EXTRANJEROS', 'Lunes a Sàbado: 09:00 a 17:00', 'N/A', '1', -0.22044900000000001, -78.512564999999995, 'museoCatedral.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (24, 'Museo María Augusta Urrutia', 'La Casa Museo María Augusta Urrutia. que muestra la forma de vida de la aristocracia quiteña en la década de los años 20. del siglo pasado. y además reconoce la labor humanitaria de una mujer que consagró su vida a la obra social.', 'Adulto  2.00. Tercera edad 1.00. Niños hasta 12 años 0.50', 'Martes a viernes: 10:00 a 18:00. Sàbado. domingo y feriados de 09:30 a 17:00', 'N/A', '1', -0.22154199999999999, -78.513908000000001, 'museoMaria.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (25, 'Museo Manuela Sáenz', 'En un recorrido por el museo se puede conocer la vida de la heroína quiteña entrelazada con los grandes héroes de la independencia. Bolívar y Sucre. Manuelita. considerada como la mujer de América. ella que cabalgó los Andes buscando la libertad de nuestros países', 'Adulto  4.00. Estudiantes. tercera edad 2.00. Niños hasta 12 años 1.00', 'Miércoles a domingo de 10h00 a 17h00', 'N/A', '1', -0.222831, -78.510493999999994, 'museoManuelaSaenz.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (26, 'Museo de Arte Colonial', 'El guión del Museo gira sobre la Vida y el Arte en el Quito de la época virreinal se analiza la ciudad y sus vivencias en ese período. cuyo eje dinamizador de la sociedad y el arte es la marcada religiosidad de la época. expresada en sus costumbres. devociones y festividades', 'Entrada libre', 'Martes a Sábado: 09:00 a 17:00', 'N/A', '1', -0.21765599999999999, -78.513047999999998, 'museoArteColonial.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (27, 'Centro Cultural Metropolitano', 'El Centro Cultural Metropolitano es una institución de servicio público adscrita  a la Secretaría de Cultura del Municipio de Quito. cuya  misión  es contribuir al conocimiento. valoración. promoción y disfrute del patrimonio y las diversas  manifestaciones culturales. y a la generación e intercambio de conocimientos.', 'Entrada Libre', '"Martes a Sàbado: 09:00 a 18:00. Domingo: 09:00 a 13:0"', 'N/A', '1', -0.22017400000000001, -78.513144999999994, 'museoMetropolitano.jpg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (28, 'Museo del Pasillo', 'El Museo – Escuela del Pasillo nace como una propuesta que resalta y reconoce la diversidad cultural. el principio de igual dignidad y respeto para todas las culturas y manifestaciones musicales y abarca una visión general de las músicas del Ecuador. con énfasis en el género musical pasillo.', 'Entrada Libre', 'Martes a domingo de 09h00 a 16h00 ', 'N/A', '1', -0.22193199999999999, -78.514240999999998, 'museoPasillo.jpeg', 2, 4);
INSERT INTO public."lugar-turistico" (id, nombre, "descripcionLugar", precio, horario, "descripcionAccesibilidad", valoracion, latitud, longitud, foto_lugar, "tipoLugar", "tipoDiscapacidad") VALUES (29, 'La Ronda', 'La tradicional calle Morales. más conocida como La Ronda. se la conoce por su importancia histórica y cultural como una de las zonas más emblemáticas de Quito. donde se asentó la llamada “filosofía bohemia” en sus fondas. Músicos. canciones icónicas. grandes bebedores. grandes poetas. surcaron sus aceras. debajo de los pintorescos balcones que siguen intactos hoy. decorados con hermosos geranios. ', 'N/A', 'N/A', 'N/A', '1', -0.22403600000000001, -78.514516, 'laRonda.jpg', 6, 4);
